CREATE TABLE PRODUCTO (ID INTEGER AUTO_INCREMENT NOT NULL, DESCRIPCION VARCHAR(1000), NOMBRE VARCHAR(255), PRECIO DOUBLE, cantidad INTEGER, PRIMARY KEY (ID))
CREATE TABLE USUARIO (DNI VARCHAR(255) NOT NULL, ALTURA DOUBLE, APELLIDOS VARCHAR(255), FECHA_NACIMIENTO DATE, NOMBRE VARCHAR(100), producto_mas_caro INTEGER, PRIMARY KEY (DNI))
CREATE TABLE Opiniones (Producto_ID INTEGER, opinion VARCHAR(255))
ALTER TABLE USUARIO ADD CONSTRAINT FK_USUARIO_producto_mas_caro FOREIGN KEY (producto_mas_caro) REFERENCES PRODUCTO (ID)
ALTER TABLE Opiniones ADD CONSTRAINT FK_Opiniones_Producto_ID FOREIGN KEY (Producto_ID) REFERENCES PRODUCTO (ID)
